import { Typography, TextField, Button } from '@mui/material';
import Navigation from '../../components/Navigation';
import { useEffect, useState } from 'react';

function CreateCartoons() {
  const [formData, setFormData] = useState({ name: '', description: '' });

  const [title, setTitle] = useState('Create Cartoons');

  useEffect(() => {
    const newTitle = `Create Cartoon - ${formData.name}`;

    setTitle(newTitle);
  }, [formData.name]);

  useEffect(() => {
    setTitle('Create Cartoon');
    return () => {
      console.log('destroy');
    };
  }, []);

  const handleFormSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    setFormData({ name: '', description: '' });
  };

  const handleFormChange = (e) => {
    console.log(e.target.value);
    const newFormData = { ...formData };
    console.log(e.target.name);
    newFormData[e.target.name] = e.target.value;

    setFormData(newFormData);
  };

  return (
    <>
      <Navigation />
      <Typography variant="h1" gutterBottom>
        {title}
      </Typography>
      <form>
        {/* <TextField id="filled-basic" label="Name" variant="filled"> */}
        <input
          value={formData.name}
          type="text"
          name="name"
          placeholder="Name"
          onChange={handleFormChange}
        />
        {/* </TextField> */}
        {/* <TextField id="filled-basic" label="Description" variant="filled"> */}
        <input
          value={formData.description}
          type="text"
          name="description"
          placeholder="Description"
          onChange={handleFormChange}
        />
        {/* </TextField> */}
        <Button variant="contained" type="submit" onClick={handleFormSubmit}>
          {/* <button type="submit" onClick={handleFormSubmit}> */}
          Save
          {/* </button> */}
        </Button>
      </form>
    </>
  );
}

export default CreateCartoons;
