import { Modal, Box, Typography } from '@mui/material';
import { useState } from 'react';
// import { Button } from '@mui/material';
import Grid from '@mui/material/Grid';
import './Modal.css';

import Navigation from '../../components/Navigation';
import CartoonCard from './CartoonCard';

const cartoons = [
  { name: 'Sailor Moon', description: 'White', image: 'https://picsum.photos/200' },
  { name: 'Tom & Jerry', description: 'Blues', image: 'https://picsum.photos/200' },
  { name: 'Curaj-Caine Fricos', description: 'Black', image: 'https://picsum.photos/200' },
  { name: 'Rick and Morty', description: 'Orange', image: 'https://picsum.photos/200' },
  { name: 'Cow and chicken', description: 'Purple', image: 'https://picsum.photos/200' },
];

function Cartoons() {
  const [open, setOpen] = useState(false);

  const [currentCartoon, setCurrentCartoon] = useState({});
  const handleClose = () => {
    setOpen(false);
  };

  const handleOpenModal = (title, image, description) => {
    setOpen(!open);
    setCurrentCartoon({ title, image, description });
  };

  return (
    <>
      <Navigation />
      <Typography variant="h1" gutterBottom style={{ marginTop: 100 }}>
        Cartoons
      </Typography>
      {cartoons.map((cartoon) => {
        return (
          <Grid container spacing={2}>
            <Grid item xs={2}>
              <div>
                <CartoonCard
                  title={cartoon.name}
                  image={cartoon.image}
                  description={cartoon.description}
                  key={cartoon.name}
                  handleOpenModal={handleOpenModal}
                />
              </div>
            </Grid>
          </Grid>
        );
      })}

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box className="modalPosition">
          <Typography id="modal-modal-title" variant="h6" component="h2" className="modalContent">
            {currentCartoon.title}
          </Typography>
          <img src={currentCartoon.image} className="modalContent" />
          <Typography
            id="modal-modal-description"
            variant="body2"
            sx={{ mt: 2 }}
            className="modalContent"
          >
            {currentCartoon.description}
          </Typography>
        </Box>
      </Modal>
    </>
  );
}

export default Cartoons;
